const MarkdownToRender = require('./index');
const path = require('path');

// Create an instance of the markdown renderer with a more colorful theme
const renderer = new MarkdownToRender({
  codeTheme: 'atom-one-dark' // Very colorful theme option
});

// Get file paths
const inputFile = path.join(__dirname, 'sample.md');
const outputFile = path.join(__dirname, 'output.html');

try {
  // Render the markdown file to HTML
  const result = renderer.renderFile(inputFile, outputFile);
  console.log(`Markdown successfully rendered to: ${result}`);
} catch (error) {
  console.error('Error converting markdown:', error);
} 